    private void comparebtn() {
        int player = 0;
        //有某行全部等於圈或等於叉，找出贏的那排並塗色
        if ((table[0] == 1 && table[1] == 1 && table[2] == 1)
                || (table[3] == 1 && table[4] == 1 && table[5] == 1)
                || (table[6] == 1 && table[7] == 1 && table[8] == 1)
                || (table[0] == 1 && table[3] == 1 && table[6] == 1)
                || (table[1] == 1 && table[4] == 1 && table[7] == 1)
                || (table[2] == 1 && table[5] == 1 && table[8] == 1)
                || (table[2] == 1 && table[4] == 1 && table[6] == 1)
                || (table[0] == 1 && table[4] == 1 && table[8] == 1)
                || (table[0] == 2 && table[1] == 2 && table[2] == 2)
                || (table[3] == 2 && table[4] == 2 && table[5] == 2)
                || (table[6] == 2 && table[7] == 2 && table[8] == 2)
                || (table[0] == 2 && table[3] == 2 && table[6] == 2)
                || (table[1] == 2 && table[4] == 2 && table[7] == 2)
                || (table[2] == 2 && table[5] == 2 && table[8] == 2)
                || (table[2] == 2 && table[4] == 2 && table[6] == 2)
                || (table[0] == 2 && table[4] == 2 && table[8] == 2)) {
            for (int l = 1; l <= 2; l++) {
                if (table[0] == l && table[1] == l && table[2] == l) {
                    player = l;
                    mainbt0.setBackground(Color.green);
                    mainbt1.setBackground(Color.green);
                    mainbt2.setBackground(Color.green);
                }
                if (table[3] == l && table[4] == l && table[5] == l) {
                    player = l;
                    mainbt3.setBackground(Color.green);
                    mainbt4.setBackground(Color.green);
                    mainbt5.setBackground(Color.green);
                }
                if (table[6] == l && table[7] == l && table[8] == l) {
                    player = l;
                    mainbt6.setBackground(Color.green);
                    mainbt7.setBackground(Color.green);
                    mainbt8.setBackground(Color.green);
                }
                if (table[0] == l && table[3] == l && table[6] == l) {
                    player = l;
                    mainbt0.setBackground(Color.green);
                    mainbt3.setBackground(Color.green);
                    mainbt6.setBackground(Color.green);
                }
                if (table[1] == l && table[4] == l && table[7] == l) {
                    player = l;
                    mainbt1.setBackground(Color.green);
                    mainbt4.setBackground(Color.green);
                    mainbt7.setBackground(Color.green);
                }
                if (table[2] == l && table[5] == l && table[8] == l) {
                    player = l;
                    mainbt2.setBackground(Color.green);
                    mainbt5.setBackground(Color.green);
                    mainbt8.setBackground(Color.green);
                }
                if (table[2] == l && table[4] == l && table[6] == l) {
                    player = l;
                    mainbt2.setBackground(Color.green);
                    mainbt4.setBackground(Color.green);
                    mainbt6.setBackground(Color.green);
                }
                if (table[0] == l && table[4] == l && table[8] == l) {
                    player = l;
                    mainbt0.setBackground(Color.green);
                    mainbt4.setBackground(Color.green);
                    mainbt8.setBackground(Color.green);
                }
            }
            playerwin(player);
            //如果沒有檢查是不是方格都填滿
        } else if (!(table[0] == 0
                || table[1] == 0
                || table[2] == 0
                || table[3] == 0
                || table[4] == 0
                || table[5] == 0
                || table[6] == 0
                || table[7] == 0
                || table[8] == 0)) {
            playerwin(3);
        }
    }

    //警告空格已有文字
    private void filled() {
        JOptionPane.showMessageDialog(null, "Already filled.", "Warning", JOptionPane.WARNING_MESSAGE);
    }

    //檢查傳入數據是圈獲勝、叉獲勝還是平手並彈出對話框
    private void playerwin(int player) {
        if (player == 1 || player == 2) {
            String name = "";
            switch (player) {
                case 1:
                    name = "○";
                    break;
                case 2:
                    name = "×";
                    break;
            }
            if (JOptionPane.showConfirmDialog(null, "Player \" " + name + " \"\nWin!\nReplay?", "Result", JOptionPane.YES_NO_OPTION, JOptionPane.QUESTION_MESSAGE) == JOptionPane.YES_OPTION) {
                Main.resetact();
                gamestart();
            } else {
                System.exit(0);
            }

        } else {
            if (JOptionPane.showConfirmDialog(null, "Draw.\nReplay?", "Result", JOptionPane.YES_NO_OPTION, JOptionPane.QUESTION_MESSAGE) == JOptionPane.YES_OPTION) {
                Main.resetact();
                gamestart();
            } else {
                System.exit(0);
            }
        }
    }
//開始按鈕按下後變Restart，為Restart時會有彈窗
    private void startbtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_startbtnActionPerformed
        switch (startbtn.getText()) {
            case "Start":
                startbtn.setText("Restart");
                gamestart();
                break;
            case "Restart":
                if (JOptionPane.showConfirmDialog(null, "Are You Sure to Restart Game?", "Are You Sure?", JOptionPane.YES_NO_OPTION, JOptionPane.QUESTION_MESSAGE) == JOptionPane.YES_OPTION) {
                    Main.resetact();
                    gamestart();
                }
        }
    }//GEN-LAST:event_startbtnActionPerformed

    //流程說明：
    //將按鈕上顯示的文字轉換成數字(Main中的方法)
    //之後用(Main)中的方法判斷要填入圈還是叉後修改按鈕文字

    private void mainbt0ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_mainbt0ActionPerformed
        int btnstat = Main.detbtnstat(mainbt0.getText());
        if (btnstat != 3) {
            switch (Main.btnpressedinit(0, btnstat)) {
                case 1:
                    mainbt0.setText("○");
                    comparebtn();
                    break;
                case 2:
                    mainbt0.setText("×");
                    comparebtn();
                    break;
                default:
                    filled();
                    break;
            }
        }
    }//GEN-LAST:event_mainbt0ActionPerformed

