    private void exitconfirm() {
        //顯示離開提示並請使用者確認
        if (JOptionPane.showConfirmDialog(null, "Are You Sure to EXIT?", "Are You Sure?", JOptionPane.YES_NO_OPTION, JOptionPane.QUESTION_MESSAGE) == JOptionPane.YES_OPTION) {
            System.exit(0);
        }
    }

